import React from "react";
import { Button } from "@material-ui/core";
export const Item = ({ item, delProd }) => {
  const name = item.name;
  const nos = item.amount;
  const price = item.price;
  const prefix = "Rs.";
  const isCartItem = nos > 0;
  const deleteItem = (id) => {
    delProd(id);
  };
  return (
    <li>
      {name}
      {
        isCartItem ? (
          <span>
          {Math.abs(nos)}
        {/* </span>
        <span> */}
          {prefix}
          {Math.abs(price)}
        </span>
        ) : (
          <p></p>
        )
      }
      <button onClick={() => delProd(item.id)} className="delete-btn">
        x
      </button>
      <Button onClik = {() => delProd(item.id)}>Cart</Button>
    </li>
  );
};

export default Item;
