import React from "react";

export const Item = ({ item, delProd }) => {
  const name = item.name;
  const nos = item.amount;
  const price = item.price;
  const prefix = "Rs.";
  const deleteItem = (id) => {
    delProd(id);
  };
  return (
    <li>
      {name}
      if(nos > 0){
        // <span>
        //   {Math.abs(nos)}
        // </span>
        <span>
          {prefix}
          {Math.abs(price)}
        </span>
      }
      <button onClick={() => delProd(item.id)} className="delete-btn">
        x
      </button>
    </li>
  );
};

export default Item;
